import { KeyboardArrowRight } from '@mui/icons-material'
import Link from 'next/link'
import Button from 'polarkit/components/ui/atoms/button'
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
} from 'polarkit/components/ui/atoms/card'

interface GuideCardProps {
  icon: 'nextjs' | 'node' | 'laravel' | 'figma'
  title: string
  description: string
  href: string
}

export const GuideCard = ({
  icon,
  title,
  description,
  href,
}: GuideCardProps) => {
  let iconComponent
  switch (icon) {
    case 'nextjs':
      iconComponent = <NextJSLogo />
      break
    case 'node':
      iconComponent = <NodeLogo />
      break
    case 'laravel':
      iconComponent = <LaravelLogo />
      break
    case 'figma':
      iconComponent = <FigmaLogo />
      break
    default:
      iconComponent = null
  }

  return (
    <Card className="flex flex-col">
      {icon && <CardHeader className="pb-0">{iconComponent}</CardHeader>}
      <CardContent className="flex h-full flex-col gap-y-4 py-8">
        <h3 className="my-0">{title}</h3>
        <p className="dark:text-polar-500 mb-0 leading-snug text-gray-500">
          {description}
        </p>
      </CardContent>
      <CardFooter className="pt-0">
        <Link href={href}>
          <Button
            size="sm"
            wrapperClassNames="flex flex-row items-center gap-x-1"
          >
            <span>Get Started</span>
            <KeyboardArrowRight className="text-lg" fontSize="inherit" />
          </Button>
        </Link>
      </CardFooter>
    </Card>
  )
}

